SELECT sales_docs.doc_date AS Date, sales_docs.doc_letter AS Letter, sales_docs.document_POS AS 'Point of Sale', sales_docs.document_numb AS 'Number', sales_docs.client_id AS 'Client ID', clients.client_name AS 'Client Name', sales_docs.vat_base_105 as 'VAT Base 10,5', sales_docs.vat_base_21 as 'VAT Base 21', sales_docs.vat_base_27 as 'VAT Base 27', sales_docs.vat_105 AS 'VAT 10,5', sales_docs.vat_21 AS 'VAT 21', sales_docs.vat_27 AS 'VAT 27', sales_docs.vat_withholdings AS 'VAT Withholdings', sales_docs.gross_income_withholdings AS 'Gross Income Withholdings', sales_docs.other_withholdings AS 'Other Withholdings', sales_docs.other_amounts_not_vat_Base AS 'Other Amounts', sales_docs.total_document_amount AS 'Total' FROM sales_docs INNER JOIN clients ON clients.client_id = sales_docs.client_id